export interface Module {
  id: string | number
  icon: React.ReactNode
  title: string
  subtitle: string
  mascotCopy?: string
  status: ModuleStatus
  lessons: Lesson[]
}

export interface HomeScreenProps extends React.HTMLAttributes<HTMLDivElement> {
