          motion: MascotMotion.SPEAKING,
          durationMs: 30000,
          lines: [
            mascotText,
            currentModule?.title ? `Today: ${currentModule.title}` : "",
            "Pick a lesson and go step-by-step",
            "Watch for key terms and exceptions",
          ].filter(Boolean) as string[],
        },
        {
          motion: MascotMotion.IDLE,
          durationMs: null,
          lines: [
