          motion: MascotMotion.THINKING,
          durationMs: 450,
          lines: ["Hmmâ€¦", "Let me think", "One moment"],
        },
        {
          motion: MascotMotion.SPEAKING,
          durationMs: 4200,
          lines: [mascotText, "Letâ€™s jump in"].filter(Boolean) as string[],
        },
