import * as React from "react"
import { cn } from "@/lib/utils"
import { QuizHeader } from "../composite/QuizHeader"
import { QuizQuestion } from "../composite/QuizQuestion"
import { QuizFeedback } from "../composite/QuizFeedback"
import { QuizMascotPrompt, type QuizMascotScriptStep, type QuizMascotStreamConfig } from "../composite/QuizMascotPrompt"
import { Answer } from "../atomic/Answer"
import { Button } from "@/components/ui/legali/button"
import { MascotMotion } from "../mascot"

export interface AnswerOption {
  id: string | number
  text: string
  correct: boolean
}

export interface Question {
  id: string | number
  question: string
  answers: AnswerOption[]
  explanation: string

  typing?: {
    /** Typing speed for the question (ms per character). */
    speedMs?: number
    /** Whether to show the cursor while typing the question. */
    showCursor?: boolean
  }

  mascot?: {
    /** Looping script that runs while the question is typing (pre-hint, pre-feedback). */
    onReading?: QuizMascotScriptStep[]
    /** Looping script that runs after the question finishes typing (until feedback). */
    script?: QuizMascotScriptStep[]
    /** One-shot script to run (after question typing) when the answer is correct. */
    onRevealCorrect?: QuizMascotScriptStep[]
    /** One-shot script to run (after question typing) when the answer is incorrect. */
    onRevealIncorrect?: QuizMascotScriptStep[]
    /** TypingText behavior for mascot lines. */
    stream?: QuizMascotStreamConfig
  }
}

export interface QuizScreenProps extends React.HTMLAttributes<HTMLDivElement> {
  questions: Question[]
  onClose: () => void
  onQuizComplete: (score: number, total: number, userAnswers: Record<string | number, string | number>) => void
}

const QuizScreen = React.forwardRef<HTMLDivElement, QuizScreenProps>(
  ({ className, questions, onClose, onQuizComplete, ...props }, ref) => {
    const [currentQuestionIndex, setCurrentQuestionIndex] = React.useState(0)
    const [selectedAnswerId, setSelectedAnswerId] = React.useState<string | number | null>(null)
    const [userAnswers, setUserAnswers] = React.useState<Record<string | number, string | number>>({})
    const [isAnswerChecked, setIsAnswerChecked] = React.useState(false)
    const [score, setScore] = React.useState(0)
    const [showFeedback, setShowFeedback] = React.useState(false)

    const [questionTypedDone, setQuestionTypedDone] = React.useState(false)
    const [promptDelayDone, setPromptDelayDone] = React.useState(false)
    const [pendingResult, setPendingResult] = React.useState<boolean | null>(null)
    const [reactionPlayed, setReactionPlayed] = React.useState(false)

    const currentQuestion = questions?.[currentQuestionIndex]

    if (!currentQuestion) {
      return <div className="p-8 text-center text-slate-500">No questions available.</div>
    }

    const isLastQuestion = currentQuestionIndex === questions.length - 1
    const progress = ((currentQuestionIndex) / questions.length) * 100

    React.useEffect(() => {
      setQuestionTypedDone(false)
      setPromptDelayDone(false)
      setPendingResult(null)
      setReactionPlayed(false)
    }, [currentQuestion.id])

    React.useEffect(() => {
      if (showFeedback) {
        setPromptDelayDone(false)
        return
      }

      if (!questionTypedDone) {
        setPromptDelayDone(false)
        return
      }

      const timer = window.setTimeout(() => {
        setPromptDelayDone(true)
      }, 8000)

      return () => window.clearTimeout(timer)
    }, [questionTypedDone, showFeedback, currentQuestion.id])

    const defaultHintScript = React.useMemo<QuizMascotScriptStep[]>(() => {
      return [
        {
          motion: MascotMotion.SPEAKING,
          durationMs: 2800,
          lines: ["Quick tip:", "Look for jurisdiction, money limits, and timing."],
        },
        {
          motion: MascotMotion.THINKING,
          durationMs: null,
          lines: ["Eliminate obvious mismatches.", "Then choose the best fit."],
        },
      ]
    }, [])

    const defaultReadingScript = React.useMemo<QuizMascotScriptStep[]>(() => {
      return [
        {
          motion: MascotMotion.SPEAKING,
          durationMs: 2600,
          lines: ["Letâ€™s read this carefully.", "Focus on the key detail."],
        },
        {
          motion: MascotMotion.SPEAKING,
          durationMs: null,
          lines: ["Take your time.", "Then choose the best answer."],
        },
      ]
    }, [])

    const defaultCorrectScript = React.useMemo<QuizMascotScriptStep[]>(() => {
      return [
        {
          motion: MascotMotion.CELEBRATE,
          durationMs: 2200,
          lines: ["Nice!", "Thatâ€™s the right idea."],
        },
        {
          motion: MascotMotion.SPEAKING,
          durationMs: 2600,
          lines: ["Keep going â€” one question at a time."],
        },
      ]
    }, [])

    const defaultIncorrectScript = React.useMemo<QuizMascotScriptStep[]>(() => {
      return [
        {
          motion: MascotMotion.CONFUSED,
          durationMs: 2400,
          lines: ["Close â€” but not quite.", "Letâ€™s use the explanation to lock it in."],
        },
        {
          motion: MascotMotion.SPEAKING,
          durationMs: 2600,
          lines: ["Youâ€™ll spot this pattern next time."],
        },
      ]
    }, [])

    const hintScript = React.useMemo(() => {
      return currentQuestion.mascot?.script?.length ? currentQuestion.mascot.script : defaultHintScript
    }, [currentQuestion.mascot?.script, defaultHintScript])

    const readingScript = React.useMemo(() => {
      return currentQuestion.mascot?.onReading?.length
        ? currentQuestion.mascot.onReading
        : defaultReadingScript
    }, [currentQuestion.mascot?.onReading, defaultReadingScript])

    const correctScript = React.useMemo(() => {
      return currentQuestion.mascot?.onRevealCorrect?.length
        ? currentQuestion.mascot.onRevealCorrect
        : defaultCorrectScript
    }, [currentQuestion.mascot?.onRevealCorrect, defaultCorrectScript])

    const incorrectScript = React.useMemo(() => {
      return currentQuestion.mascot?.onRevealIncorrect?.length
        ? currentQuestion.mascot.onRevealIncorrect
        : defaultIncorrectScript
    }, [currentQuestion.mascot?.onRevealIncorrect, defaultIncorrectScript])

    const shouldPlayReaction =
      showFeedback && pendingResult != null && questionTypedDone && !reactionPlayed

    React.useEffect(() => {
      if (!shouldPlayReaction) return

      const steps = pendingResult ? correctScript : incorrectScript
      const totalMs = steps.reduce((sum, s) => sum + (s.durationMs ?? 0), 0)
      const timer = window.setTimeout(() => {
        setReactionPlayed(true)
      }, Math.max(1200, totalMs))

      return () => window.clearTimeout(timer)
    }, [shouldPlayReaction, pendingResult, correctScript, incorrectScript])

    const handleAnswerSelect = (id: string | number) => {
      if (isAnswerChecked) return
      setSelectedAnswerId(id)
    }

    const handleCheck = () => {
      if (!selectedAnswerId) return

      const isCorrect = Boolean(currentQuestion.answers.find(a => a.id === selectedAnswerId)?.correct)
      if (isCorrect) setScore(s => s + 1)

      setUserAnswers(prev => ({
        ...prev,
        [currentQuestion.id]: selectedAnswerId
      }))

      setIsAnswerChecked(true)
      setShowFeedback(true)

      setPendingResult(isCorrect)
    }

    const handleContinue = () => {
      if (isLastQuestion) {
        onQuizComplete(
          score + (currentQuestion.answers.find(a => a.id === selectedAnswerId)?.correct && !isAnswerChecked ? 1 : 0), // Handle case where check wasn't clicked if that's possible (though UI prevents it) - actually logic above sets score on check.
          questions.length,
          userAnswers
        )
      } else {
        setCurrentQuestionIndex(i => i + 1)
        setSelectedAnswerId(null)
        setIsAnswerChecked(false)
        setShowFeedback(false)

        setQuestionTypedDone(false)
        setPendingResult(null)
        setReactionPlayed(false)
      }
    }

    const promptActive = React.useMemo(() => {
      if (showFeedback && pendingResult != null) return true
      if (showFeedback) return false
      return true
    }, [pendingResult, showFeedback])

    const promptTriggerKey = React.useMemo(() => {
      const resultKey = pendingResult == null ? "none" : pendingResult ? "correct" : "incorrect"
      const phaseKey = showFeedback ? "feedback" : !promptDelayDone ? "reading" : "hint"
      return `${String(currentQuestion.id)}:${phaseKey}:${resultKey}`
    }, [currentQuestion.id, pendingResult, promptDelayDone, showFeedback])

    const promptScript = React.useMemo<QuizMascotScriptStep[]>(() => {
      if (shouldPlayReaction) return pendingResult ? correctScript : incorrectScript
      if (showFeedback && pendingResult != null) return pendingResult ? correctScript : incorrectScript
      if (!promptDelayDone) return readingScript
      return hintScript
    }, [
      correctScript,
      hintScript,
      incorrectScript,
      pendingResult,
      promptDelayDone,
      readingScript,
      shouldPlayReaction,
      showFeedback,
    ])

    const promptStream = React.useMemo<QuizMascotStreamConfig>(() => {
      const base = currentQuestion.mascot?.stream ?? {}
      return {
        ...base,
        loop: shouldPlayReaction ? false : true,
      }
    }, [currentQuestion.mascot?.stream, shouldPlayReaction])

    const inactiveMotion = React.useMemo(() => {
      if (showFeedback) {
        if (pendingResult === true) return MascotMotion.CELEBRATE
        return MascotMotion.IDLE
      }
      return questionTypedDone ? MascotMotion.IDLE : MascotMotion.WRITING
    }, [pendingResult, questionTypedDone, showFeedback])

    return (
      <div
        ref={ref}
        className={cn(
          "flex flex-col h-full min-h-screen relative overflow-hidden",
          // New Bluish Gradient Background
          "bg-gradient-to-br from-sky-100 via-blue-50 to-white",
          className
        )}
        {...props}
      >
        {/* Decorative Orbs */}
        <div className="absolute top-[-10%] right-[-10%] w-[600px] h-[600px] bg-sky-200/40 rounded-full blur-3xl pointer-events-none animate-pulse" />
        <div className="absolute bottom-[-10%] left-[-10%] w-[500px] h-[500px] bg-blue-200/40 rounded-full blur-3xl pointer-events-none animate-pulse delay-700" />

        <QuizHeader
          onClose={onClose}
          currentQuestion={currentQuestionIndex + 1}
          totalQuestions={questions.length}
          progress={progress}
          className="bg-white/40 backdrop-blur-md border-white/20 sticky top-0 z-10"
        />

        <div className="flex-1 overflow-y-auto px-5 py-8 pb-32 relative z-0">
          <div className="max-w-2xl mx-auto">


            <QuizQuestion
              question={currentQuestion.question}
              questionNumber={currentQuestionIndex + 1}
              typingKey={currentQuestion.id}
              typingSpeedMs={70}
              showCursor={currentQuestion.typing?.showCursor ?? false}
              onTypedComplete={() => setQuestionTypedDone(true)}
              className="mb-8"
            />
            <QuizMascotPrompt
              className="mb-6"
              active={promptActive}
              triggerKey={promptTriggerKey}
              script={promptScript}
              stream={promptStream}
              inactiveMotion={inactiveMotion}
              mascotWidth={240}
              mascotHeight={240}
            />

            <div className="space-y-3 animate-slide-in-from-bottom">
              {currentQuestion.answers.map((answer, index) => (
                <Answer
                  key={answer.id}
                  selected={selectedAnswerId === answer.id}
                  correct={isAnswerChecked && answer.correct}
                  incorrect={isAnswerChecked && selectedAnswerId === answer.id && !answer.correct}
                  onClick={() => handleAnswerSelect(answer.id)}
                  disabled={isAnswerChecked}
                  shortcut={String.fromCharCode(65 + index)} // A, B, C, D
                  className="animate-fade-in bg-white/60 backdrop-blur-sm border-white/40 hover:bg-white/80"
                >
                  {answer.text}
                </Answer>
              ))}
            </div>
          </div>
        </div>

        {/* Bottom Action Bar */}
        {!showFeedback && (
          <div className="fixed bottom-0 left-0 right-0 p-5 bg-white/30 backdrop-blur-xl border-t border-white/20 z-10">
            <div className="max-w-2xl mx-auto">
              <Button
                onClick={handleCheck}
                disabled={!selectedAnswerId}
                className={cn(
                  "w-full font-bold py-6 text-lg rounded-2xl shadow-xl transition-all duration-300",
                  selectedAnswerId 
                    ? "bg-blue-600 hover:bg-blue-700 text-white shadow-blue-500/30 hover:-translate-y-1 hover:shadow-2xl" 
                    : "bg-slate-200 text-slate-400 cursor-not-allowed"
                )}
              >
                Check Answer
              </Button>
            </div>
          </div>
        )}

        <QuizFeedback
          show={showFeedback}
          correct={currentQuestion.answers.find(a => a.id === selectedAnswerId)?.correct || false}
          explanation={currentQuestion.explanation}
          onContinue={handleContinue}
          className="backdrop-blur-xl bg-white/80 border-t border-white/40"
        />
      </div>
    )
  }
)
QuizScreen.displayName = "QuizScreen"

export { QuizScreen }

import * as React from "react"
import { cn } from "@/lib/utils"
import { Answer } from "../atomic/Answer"
import { ExplanationCard } from "../atomic/ExplanationCard"
import { QuestionNumberBadge } from "../atomic/QuestionNumberBadge"
import { Button } from "@/components/ui/legali/button"
import { ArrowLeft } from "lucide-react"
import { Question } from "./QuizScreen"

export interface ReviewScreenProps extends React.HTMLAttributes<HTMLDivElement> {
  questions: Question[]
  userAnswers: Record<string | number, string | number>
  onClose: () => void
}

export const ReviewScreen = React.forwardRef<HTMLDivElement, ReviewScreenProps>(
  ({ className, questions, userAnswers, onClose, ...props }, ref) => {
    return (
      <div
        ref={ref}
        className={cn(
          "flex flex-col h-full min-h-screen bg-slate-50",
          className
        )}
        {...props}
      >
        {/* Header */}
        <div className="sticky top-0 z-10 bg-white/80 backdrop-blur-md border-b border-slate-200 px-5 py-4 flex items-center gap-4">
            <Button variant="ghost" size="icon" onClick={onClose} className="-ml-2">
                <ArrowLeft className="w-5 h-5" />
            </Button>
            <h1 className="text-lg font-bold text-slate-900">Review Answers</h1>
        </div>

        <div className="flex-1 overflow-y-auto px-5 py-8 space-y-12">
            <div className="max-w-2xl mx-auto space-y-12">
                {questions.map((question, index) => {
                    const userAnswerId = userAnswers[question.id]
                    const isCorrect = question.answers.find(a => a.id === userAnswerId)?.correct

                    return (
                        <div key={question.id} className="space-y-6">
                            <div className="flex items-start gap-4">
                                <QuestionNumberBadge 
                                    number={index + 1} 
                                    status={isCorrect ? 'correct' : 'incorrect'} 
                                />
                                <div className="flex-1">
                                    <h3 className="text-lg font-medium text-slate-900 mb-4">{question.question}</h3>

                                    <div className="space-y-3">
                                        {question.answers.map((answer, aIndex) => {
                                            const isSelected = userAnswerId === answer.id
                                            const isAnswerCorrect = answer.correct

                                            return (
                                                <Answer
                                                    key={answer.id}
                                                    selected={isSelected}
                                                    correct={isAnswerCorrect}
                                                    incorrect={isSelected && !isAnswerCorrect}
                                                    disabled={true}
                                                    shortcut={String.fromCharCode(65 + aIndex)}
                                                    className="opacity-100 cursor-default hover:translate-y-0 hover:shadow-none"
                                                >
                                                    {answer.text}
                                                </Answer>
                                            )
                                        })}
                                    </div>

                                    <ExplanationCard explanation={question.explanation} />
                                </div>
                            </div>
                            {index < questions.length - 1 && <div className="h-px bg-slate-200" />}
                        </div>
                    )
                })}
            </div>
        </div>

        <div className="p-5 border-t border-slate-200 bg-white">
            <div className="max-w-2xl mx-auto">
                <Button onClick={onClose} className="w-full py-6 text-lg rounded-2xl">
                    Return to Results
                </Button>
            </div>
        </div>
      </div>
    )
  }
)

ReviewScreen.displayName = "ReviewScreen"

import * as React from "react"
import { cn } from "@/lib/utils"
import { ResultsCard } from "../composite/ResultsCard"
import { GlassCard } from "../atomic/GlassCard"

export interface ResultsScreenProps extends React.HTMLAttributes<HTMLDivElement> {
  score: number
  totalQuestions: number
  badgeTitle?: string
  onContinue: () => void
  onReviewMistakes?: () => void
  streak?: number
  xpEarned?: number
}

const ResultsScreen = React.forwardRef<HTMLDivElement, ResultsScreenProps>(
  ({ className, score, totalQuestions, badgeTitle, onContinue, onReviewMistakes, streak = 5, xpEarned, ...props }, ref) => {
    // Calculate stats based on score
    const percentage = Math.round((score / totalQuestions) * 100)
    const finalXpEarned = xpEarned ?? (score * 10 + 20) // Base XP + Bonus

    return (
      <div
        ref={ref}
        className={cn(
          "flex flex-col h-full min-h-screen relative overflow-hidden items-center justify-center p-6",
          // Celebratory Blue/Gold Gradient
          "bg-[radial-gradient(ellipse_at_center,_var(--tw-gradient-stops))] from-sky-100 via-blue-50 to-white",
          className
        )}
        {...props}
      >
        {/* Decorative Confetti/Orbs */}
        <div className="absolute top-0 left-0 w-full h-full overflow-hidden pointer-events-none">
           <div className="absolute top-[10%] left-[10%] w-64 h-64 bg-blue-400/20 rounded-full blur-3xl animate-pulse" />
           <div className="absolute bottom-[10%] right-[10%] w-64 h-64 bg-sky-400/20 rounded-full blur-3xl animate-pulse delay-700" />
           <div className="absolute top-[20%] right-[20%] w-32 h-32 bg-yellow-400/30 rounded-full blur-2xl animate-bounce duration-[3000ms]" />
        </div>

        <GlassCard 
          intensity="high" 
          className="w-full max-w-md rounded-3xl shadow-2xl shadow-blue-500/10 border-white/60"
        >
          <ResultsCard
            score={score}
            totalQuestions={totalQuestions}
            xpEarned={finalXpEarned}
            accuracy={percentage}
            streak={streak}
            badgeTitle={badgeTitle}
            onContinue={onContinue}
            onReviewMistakes={onReviewMistakes}
            className="bg-transparent" // Allow GlassCard background to show
          />
        </GlassCard>
      </div>
    )
  }
)
ResultsScreen.displayName = "ResultsScreen"

export { ResultsScreen }

/**
 * LegaliMascot Component
 *
 * A React wrapper for the Rive mascot animation with support for
 * View Model data binding and motion state control.
 *
 * @module LegaliMascot
 */

import { useRive, Layout, Fit, Alignment } from '@rive-app/react-canvas'
import { useEffect, useRef } from 'react'

/**
 * Available motion states for the mascot animation.
 * These correspond to the MOTIONS enum in the Rive View Model.
 */
export const MascotMotion = {
  EXIT: 'EXIT',
  NORMAL: 'NORMAL',
  LAPTOP: 'LAPTOP',
  WRITING: 'WRITING',
  IDLE: 'IDLE',
  THINKING: 'THINKING',
  CRYING: 'CRYING',
  SPEAKING: 'SPEAKING',
  CONFUSED: 'CONFUSED',
  WAVING: 'WAVING',
  SHRUG: 'SHRUG',
  CELEBRATE: 'CELEBRATE',
} as const

export type MascotMotionType = (typeof MascotMotion)[keyof typeof MascotMotion]

/**
 * Human-readable labels for each motion state.
 */
export const MascotMotionLabels: Record<MascotMotionType, string> = {
  [MascotMotion.EXIT]: 'Exit',
  [MascotMotion.NORMAL]: 'Normal',
  [MascotMotion.LAPTOP]: 'Laptop',
  [MascotMotion.WRITING]: 'Writing',
  [MascotMotion.IDLE]: 'Idle',
  [MascotMotion.THINKING]: 'Thinking',
  [MascotMotion.CRYING]: 'Crying',
  [MascotMotion.SPEAKING]: 'Speaking',
  [MascotMotion.CONFUSED]: 'Confused',
  [MascotMotion.WAVING]: 'Waving',
  [MascotMotion.SHRUG]: 'Shrug',
  [MascotMotion.CELEBRATE]: 'Celebrate',
}

export interface LegaliMascotProps {
  /**
   * The current motion/animation state to display.
   * @default MascotMotion.IDLE
   */
  motion?: MascotMotionType

  /**
   * Whether the mascot should blink.
   * @default true
   */
  isBlink?: boolean

  /**
   * Width of the canvas container.
   * @default 300
   */
  width?: number | string

  /**
   * Height of the canvas container.
   * @default 300
   */
  height?: number | string

  /**
   * Path to the .riv animation file.
   * @default '/srchttps://raw.githubusercontent.com/rryando/legali-ai-components/main/public/animations/legali.riv'
   */
  src?: string

  /**
   * Name of the state machine to use.
   * @default 'SM_MASCOT'
   */
  stateMachine?: string

  /**
   * Additional CSS class names for the container.
   */
  className?: string

  /**
   * Callback fired when the Rive animation is loaded.
   */
  onLoad?: () => void

  /**
   * Callback fired when the motion state changes.
   */
  onMotionChange?: (motion: MascotMotionType) => void
}

/**
 * LegaliMascot Component
 *
 * Displays an animated mascot character using Rive animations.
 * The mascot supports multiple motion states and blinking control
 * through View Model data binding.
 */
export function LegaliMascot({
  motion = MascotMotion.IDLE,
  isBlink = true,
  width = 300,
  height = 300,
  src = 'https://raw.githubusercontent.com/rryando/legali-ai-components/main/public/animations/legali.riv',
  stateMachine = 'SM_MASCOT',
  className = '',
  onLoad,
  onMotionChange,
}: LegaliMascotProps) {
  // Store references to view model properties
  const blinkPropertyRef = useRef<any>(null)
  const animatesPropertyRef = useRef<any>(null)
  const isInitializedRef = useRef(false)

  const handleRiveLoad = () => {
    // This function is called when the Rive animation is loaded.
    // The actual initialization logic is in the useEffect hook.
    // We can use this for external callbacks if needed.
  }

  const { rive, RiveComponent } = useRive({
    src,
    stateMachines: stateMachine,
    layout: new Layout({
      fit: Fit.Contain,
      alignment: Alignment.Center,
    }),
    autoplay: true,
    autoBind: true, // Enable auto-binding to the default view model instance
    onLoad: handleRiveLoad,
  })

  // Initialize view model properties when rive becomes available
  useEffect(() => {
    if (rive && !isInitializedRef.current) {
      try {
        // Access the auto-bound view model instance
        const vmi = rive.viewModelInstance
        console.log('Rive loaded, viewModelInstance:', vmi)

        if (vmi) {
          // Get boolean property for isBlink
          blinkPropertyRef.current = vmi.boolean('isBlink')
          // Get enum property for animates
          animatesPropertyRef.current = vmi.enum('animates')

          console.log('Properties initialized:', {
            blink: blinkPropertyRef.current,
            animates: animatesPropertyRef.current,
          })

          // Set initial values
          if (blinkPropertyRef.current) {
            blinkPropertyRef.current.value = isBlink
          }
          if (animatesPropertyRef.current) {
            animatesPropertyRef.current.value = motion
          }

          isInitializedRef.current = true
          onLoad?.()
        }
      } catch (error) {
        console.warn('Failed to initialize view model:', error)
      }
    }
  }, [rive, isBlink, motion, onLoad])

  // Update blink state when prop changes
  useEffect(() => {
    if (blinkPropertyRef.current && isInitializedRef.current) {
      blinkPropertyRef.current.value = isBlink
    }
  }, [isBlink])

  // Track previous motion to detect changes
  const prevMotionRef = useRef<MascotMotionType>(motion)

  // Update motion state when prop changes - reset to NORMAL first
  useEffect(() => {
    if (animatesPropertyRef.current && isInitializedRef.current) {
      // Only apply reset logic if motion actually changed
      if (prevMotionRef.current !== motion) {
        // First, set to NORMAL to reset all animation attributes
        animatesPropertyRef.current.value = MascotMotion.NORMAL

        // After 300ms, apply the target motion
        const timer = setTimeout(() => {
          if (animatesPropertyRef.current) {
            animatesPropertyRef.current.value = motion
            onMotionChange?.(motion)
          }
        }, 300)

        prevMotionRef.current = motion

        return () => clearTimeout(timer)
      }
    }
  }, [motion, onMotionChange])

  return (
    <div
      className={`rive-mascot-container ${className}`}
      style={{
        width: typeof width === 'number' ? `${width}px` : width,
        height: typeof height === 'number' ? `${height}px` : height,
      }}
    >
      <RiveComponent />
    </div>
  )
}

export default LegaliMascot
