            if (item.id === "learn") iconNode = <Home className="h-6 w-6" />;
            if (item.id === "progress") iconNode = <Trophy className="h-6 w-6" />;
            if (item.id === "profile") iconNode = <User className="h-6 w-6" />;
          }

          return (
            <button
