/**
 * @see {@link https://raw.githubusercontent.com/rryando/legali-ai-components/main/registry/legali-spotlight-card.json} — Install:
 * @example npx shadcn@latest add "https://raw.githubusercontent.com/rryando/legali-ai-components/main/registry/legali-spotlight-card.json"
 */
import * as React from "react";
import { cn } from "@/lib/utils";

export interface SpotlightCardProps extends React.HTMLAttributes<HTMLDivElement> {
  /** Color of the spotlight effect (CSS color value) */
  spotlightColor?: string;
  /** Size of the spotlight radius in pixels */
  spotlightSize?: number;
}

/**
 * A card component with a mouse-follow spotlight effect.
 * Creates an interactive glow that follows the cursor.
 */
const SpotlightCard = React.forwardRef<HTMLDivElement, SpotlightCardProps>(
  (
    {
      children,
      className,
      spotlightColor = "rgba(78, 174, 208, 0.15)",
      spotlightSize = 600,
      ...props
    },
    ref
  ) => {
    const divRef = React.useRef<HTMLDivElement>(null);
    const [position, setPosition] = React.useState({ x: 0, y: 0 });
    const [opacity, setOpacity] = React.useState(0);

    // Merge refs
    React.useImperativeHandle(ref, () => divRef.current as HTMLDivElement);

    const handleMouseMove = (e: React.MouseEvent<HTMLDivElement>) => {
      if (!divRef.current) return;
      const rect = divRef.current.getBoundingClientRect();
      setPosition({ x: e.clientX - rect.left, y: e.clientY - rect.top });
      setOpacity(1);
    };

    const handleMouseEnter = () => setOpacity(1);
    const handleMouseLeave = () => setOpacity(0);

    return (
      <div
        className={cn(
          "relative overflow-hidden rounded-2xl border border-slate-200/50 bg-white/60 backdrop-blur-xl transition-all duration-300",
          "shadow-lg hover:border-slate-300/50 hover:shadow-xl",
          className
        )}
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
        onMouseMove={handleMouseMove}
        ref={divRef}
        {...props}
      >
        <div
          className="pointer-events-none absolute -inset-px opacity-0 transition-opacity duration-300"
          style={{
            opacity,
            background: `radial-gradient(${spotlightSize}px circle at ${position.x}px ${position.y}px, ${spotlightColor}, transparent 40%)`,
          }}
        />
        <div className="relative">{children}</div>
      </div>
    );
  }
);

SpotlightCard.displayName = "SpotlightCard";

export { SpotlightCard };

/**
 * @see {@link https://raw.githubusercontent.com/rryando/legali-ai-components/main/registry/legali-gradient-text.json} — Install:
 * @example npx shadcn@latest add "https://raw.githubusercontent.com/rryando/legali-ai-components/main/registry/legali-gradient-text.json"
 */
import type * as React from "react";
import { cn } from "@/lib/utils";

export type GradientTextVariant = "primary" | "danger" | "secondary" | "custom";

export interface GradientTextProps {
  /** Predefined gradient variant or 'custom' for customGradient */
  variant?: GradientTextVariant;
  /** Custom gradient CSS (used when variant is 'custom') */
  customGradient?: string;
  /** HTML element to render as */
  as?: "span" | "h1" | "h2" | "h3" | "h4" | "h5" | "h6" | "p";
  /** Additional class names */
  className?: string;
  /** Children to render */
  children?: React.ReactNode;
}

const variantGradients: Record<Exclude<GradientTextVariant, "custom">, string> = {
  primary: "from-[#4eaed0] via-[#667eea] to-[#764ba2]",
  secondary: "from-[#4eaed0] to-[#667eea]",
  danger: "from-rose-500 to-pink-500",
};

/**
 * Text component with gradient coloring.
 * Supports predefined gradients or custom CSS gradients.
 */
const GradientText = ({
  className,
  variant = "primary",
  customGradient,
  as: Component = "span",
  children,
}: GradientTextProps) => {
  const gradientClass = variant === "custom" ? "" : `bg-gradient-to-r ${variantGradients[variant]}`;

  const customStyle =
    variant === "custom" && customGradient ? { backgroundImage: customGradient } : undefined;

  return (
    <Component
      className={cn("bg-clip-text text-transparent", gradientClass, className)}
      style={customStyle}
    >
      {children}
    </Component>
  );
};

GradientText.displayName = "GradientText";

export { GradientText };

