/**
 * @see {@link https://raw.githubusercontent.com/rryando/legali-ai-components/main/registry/legali-tilt-card.json} — Install:
 * @example npx shadcn@latest add "https://raw.githubusercontent.com/rryando/legali-ai-components/main/registry/legali-tilt-card.json"
 */
import type { HTMLAttributes, MouseEvent } from "react";
import { forwardRef, useCallback, useState } from "react";
import { cn } from "@/lib/utils";

type Tilt3DState = {
  tiltX: number;
  tiltY: number;
  glarePosition: { x: number; y: number };
  isHovering: boolean;
};

type TiltCardProps = {
  /** Maximum tilt angle in degrees */
  maxTilt?: number;
  /** Perspective distance */
  perspective?: number;
  /** Enable glare effect */
  enableGlare?: boolean;
  /** Glare opacity */
  glareMaxOpacity?: number;
  /** Transition speed in ms */
  transitionSpeed?: number;
} & HTMLAttributes<HTMLDivElement>;

/**
 * Card with smooth 3D perspective tilt that follows cursor.
 * Creates a premium, Apple-like interactive effect.
 *
 * @example
 * ```tsx
 * <TiltCard maxTilt={15} enableGlare>
 *   <div className="p-8">Hover me!</div>
 * </TiltCard>
 * ```
 */
const TiltCard = forwardRef<HTMLDivElement, TiltCardProps>(
  (
    {
      className,
      children,
      maxTilt = 10,
      perspective = 1000,
      enableGlare = true,
      glareMaxOpacity = 0.15,
      transitionSpeed = 300,
      ...props
    },
    ref
  ) => {
    const [state, setState] = useState<Tilt3DState>({
      tiltX: 0,
      tiltY: 0,
      glarePosition: { x: 50, y: 50 },
      isHovering: false,
    });

    const handleMouseMove = useCallback(
      (e: MouseEvent<HTMLDivElement>) => {
        const element = e.currentTarget;
        const rect = element.getBoundingClientRect();
        const centerX = rect.left + rect.width / 2;
        const centerY = rect.top + rect.height / 2;

        const relativeX = (e.clientX - centerX) / (rect.width / 2);
        const relativeY = (e.clientY - centerY) / (rect.height / 2);

        const tiltX = relativeY * -maxTilt;
        const tiltY = relativeX * maxTilt;

        const glareX = ((e.clientX - rect.left) / rect.width) * 100;
        const glareY = ((e.clientY - rect.top) / rect.height) * 100;

        setState({
          tiltX,
          tiltY,
          glarePosition: { x: glareX, y: glareY },
          isHovering: true,
        });
      },
      [maxTilt]
    );

    const handleMouseLeave = useCallback(() => {
      setState({
        tiltX: 0,
        tiltY: 0,
        glarePosition: { x: 50, y: 50 },
        isHovering: false,
      });
    }, []);

    const cardStyle = {
      transform: `perspective(${perspective}px) rotateX(${state.tiltX}deg) rotateY(${state.tiltY}deg)`,
      transition: state.isHovering
        ? `transform ${transitionSpeed}ms cubic-bezier(0.03, 0.98, 0.52, 0.99)`
        : `transform ${transitionSpeed * 2}ms cubic-bezier(0.03, 0.98, 0.52, 0.99)`,
      transformStyle: "preserve-3d" as const,
      willChange: "transform" as const,
    };

    return (
      <div
        className={cn(
          "relative overflow-hidden rounded-2xl",
          "transition-shadow duration-300",
          state.isHovering && "shadow-2xl",
          className
        )}
        onMouseLeave={handleMouseLeave}
        onMouseMove={handleMouseMove}
        ref={ref}
        style={cardStyle}
        {...props}
      >
        {enableGlare && (
          <div
            className="pointer-events-none absolute inset-0 transition-opacity duration-300"
            style={{
              opacity: state.isHovering ? 1 : 0,
              background: `radial-gradient(
                circle at ${state.glarePosition.x}% ${state.glarePosition.y}%,
                rgba(255, 255, 255, ${glareMaxOpacity}),
                transparent 60%
              )`,
              borderRadius: "inherit",
            }}
          />
        )}
        <div className="relative" style={{ transform: "translateZ(30px)" }}>
          {children}
        </div>
      </div>
    );
  }
);

TiltCard.displayName = "TiltCard";

export { TiltCard };
export type { TiltCardProps };

/**
 * @see {@link https://raw.githubusercontent.com/rryando/legali-ai-components/main/registry/legali-animated-background.json} — Install:
 * @example npx shadcn@latest add "https://raw.githubusercontent.com/rryando/legali-ai-components/main/registry/legali-animated-background.json"
 */
import * as React from "react";
import { cn } from "@/lib/utils";
import { useParallax } from "../hooks/useAnimations";

export interface AnimatedBackgroundProps extends React.HTMLAttributes<HTMLDivElement> {
  /** Enable parallax scrolling effect */
  enableParallax?: boolean;
  /** Parallax intensity (0-1) */
  parallaxIntensity?: number;
  /** Show noise texture overlay */
  showNoise?: boolean;
}

/**
 * Animated gradient background with floating orbs.
 * Supports parallax scrolling and noise texture overlay.
 */
const AnimatedBackground = React.forwardRef<HTMLDivElement, AnimatedBackgroundProps>(
  (
    { className, enableParallax = false, parallaxIntensity = 0.3, showNoise = true, ...props },
    ref
  ) => {
    const offset = useParallax(parallaxIntensity);
    const transform = enableParallax ? `translateY(${offset}px)` : undefined;

    return (
      <div
        className={cn("pointer-events-none absolute inset-0 overflow-hidden", className)}
        ref={ref}
        {...props}
      >
        {/* Animated gradient orbs */}
        <div
          className="absolute top-0 left-1/4 h-[800px] w-[800px] animate-pulse rounded-full bg-gradient-to-br from-[#667eea]/20 via-[#764ba2]/10 to-transparent blur-3xl"
          style={{ animationDuration: "8s", transform }}
        />
        <div
          className="absolute top-1/3 right-0 h-[600px] w-[600px] animate-pulse rounded-full bg-gradient-to-bl from-[#4eaed0]/25 via-[#06b6d4]/15 to-transparent blur-3xl"
          style={{ animationDuration: "6s", animationDelay: "2s", transform }}
        />
        <div
          className="absolute bottom-0 left-0 h-[700px] w-[700px] animate-pulse rounded-full bg-gradient-to-tr from-[#f472b6]/15 via-[#ec4899]/10 to-transparent blur-3xl"
          style={{ animationDuration: "10s", animationDelay: "1s", transform }}
        />
        <div
          className="absolute right-1/4 bottom-1/4 h-[500px] w-[500px] animate-pulse rounded-full bg-gradient-to-tl from-[#a78bfa]/15 to-transparent blur-3xl"
          style={{ animationDuration: "7s", animationDelay: "3s", transform }}
        />

        {/* Noise texture overlay */}
        {showNoise && (
          <div
            className="absolute inset-0 opacity-[0.03] mix-blend-overlay"
            style={{
              backgroundImage:
                "url(\"data:image/svg+xml,%3Csvg viewBox='0 0 256 256' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noise'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.65' numOctaves='3' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noise)'/%3E%3C/svg%3E\")",
            }}
          />
        )}

        <style>{`
          @keyframes shimmer {
            0% { background-position: 200% 0; }
            100% { background-position: -200% 0; }
          }
          .animate-shimmer {
            background-size: 200% 100%;
            animation: shimmer 8s linear infinite;
          }
        `}</style>
      </div>
    );
  }
);

AnimatedBackground.displayName = "AnimatedBackground";

