/**
 * @see {@link https://raw.githubusercontent.com/rryando/legali-ai-components/main/registry/legali-contact-info-form.json} — Install:
 * @example npx shadcn@latest add "https://raw.githubusercontent.com/rryando/legali-ai-components/main/registry/legali-contact-info-form.json"
 */
import { motion } from "motion/react";
import { forwardRef, useCallback, useState } from "react";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/legali/button";
import { ContactInfoField, type ValidationState } from "../atomic/ContactInfoField";
import { GlassCard } from "../atomic/GlassCard";
import type { ContactInfo } from "../data/marketplace-types";

type ContactInfoFormProps = {
  onSubmit?: (info: ContactInfo) => void;
  className?: string;
};

function validateEmail(email: string): boolean {
  return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
}

function validatePhone(phone: string): boolean {
  return /^\+?[\d\s()-]{7,}$/.test(phone);
}

function getValidation(value: string, validator?: (v: string) => boolean): ValidationState {
  if (!value) return "idle";
  if (validator) return validator(value) ? "valid" : "invalid";
  return value.length >= 2 ? "valid" : "invalid";
}

const ContactInfoForm = forwardRef<HTMLDivElement, ContactInfoFormProps>(
  ({ className, onSubmit }, ref) => {
    const [form, setForm] = useState<ContactInfo>({
      firstName: "",
      lastName: "",
      email: "",
      phone: "",
    });

    const update = useCallback((field: keyof ContactInfo, value: string) => {
      setForm((prev) => ({ ...prev, [field]: value }));
    }, []);

    const isValid =
      form.firstName.length >= 2 &&
      form.lastName.length >= 2 &&
      validateEmail(form.email) &&
      validatePhone(form.phone);

    const handleSubmit = useCallback(() => {
      if (!isValid) return;
      onSubmit?.(form);
    }, [isValid, form, onSubmit]);

    return (
      <motion.div
        animate={{ opacity: 1, y: 0 }}
        initial={{ opacity: 0, y: 12 }}
        transition={{ duration: 0.4, ease: [0.4, 0, 0.2, 1] }}
      >
        <GlassCard className={cn("mx-4 rounded-2xl p-4", className)} intensity="medium" ref={ref}>
          <h3 className="mb-3 font-semibold text-slate-700 text-sm">Contact Details</h3>
          <div className="grid grid-cols-2 gap-3">
            <ContactInfoField
              label="First Name"
              onChange={(e) => update("firstName", e.target.value)}
              placeholder="John"
              validation={getValidation(form.firstName)}
              value={form.firstName}
            />
            <ContactInfoField
              label="Last Name"
              onChange={(e) => update("lastName", e.target.value)}
              placeholder="Smith"
              validation={getValidation(form.lastName)}
              value={form.lastName}
            />
          </div>
          <div className="mt-3 space-y-3">
            <ContactInfoField
              label="Email"
              onChange={(e) => update("email", e.target.value)}
              placeholder="john@example.com"
              type="email"
              validation={getValidation(form.email, validateEmail)}
              value={form.email}
            />
            <ContactInfoField
              label="Phone"
              onChange={(e) => update("phone", e.target.value)}
              placeholder="+1 555 123 4567"
              type="tel"
              validation={getValidation(form.phone, validatePhone)}
              value={form.phone}
            />
          </div>
          <Button
            className="mt-4 w-full rounded-xl"
            disabled={!isValid}
            onClick={handleSubmit}
            size="sm"
          >
            Continue
          </Button>
        </GlassCard>
      </motion.div>
    );
  }
);

ContactInfoForm.displayName = "ContactInfoForm";

export { ContactInfoForm };
export type { ContactInfoFormProps };

/**
 * @see {@link https://raw.githubusercontent.com/rryando/legali-ai-components/main/registry/legali-floating-mascot.json} — Install:
 * @example npx shadcn@latest add "https://raw.githubusercontent.com/rryando/legali-ai-components/main/registry/legali-floating-mascot.json"
 */
import * as React from "react";
import { cn } from "@/lib/utils";
import { useCurrentSection, useIdleDetection } from "../hooks/useAnimations";
import { LegaliMascot, MascotMotion, type MascotMotionType } from "../mascot/LegaliMascot";

export interface FloatingMascotProps extends React.HTMLAttributes<HTMLDivElement> {
  /** List of section IDs to track */
  sections?: string[];
  /** Callback when section changes */
  onSectionChange?: (section: string) => void;
  /** Idle timeout in milliseconds */
  idleTimeout?: number;
  /** Size of the mascot */
  size?: number;
  /** Number of clicks to trigger easter egg */
  easterEggClicks?: number;
  /** Easter egg message text */
  easterEggMessage?: string;
}

const sectionMotionMap: Record<string, MascotMotionType> = {
  hero: MascotMotion.WAVING,
  problem: MascotMotion.THINKING,
  features: MascotMotion.LAPTOP,
  "how-it-works": MascotMotion.IDEA,
  testimonials: MascotMotion.THUMBSUP,
  faq: MascotMotion.READING,
  cta: MascotMotion.CELEBRATE,
};

/**
 * A floating mascot that changes pose based on the current section.
 * Includes idle detection and easter egg functionality.
 */
const FloatingMascot = React.forwardRef<HTMLDivElement, FloatingMascotProps>(
  (
    {
      className,
      sections = ["hero", "problem", "features", "how-it-works", "testimonials", "faq", "cta"],
      onSectionChange,
      idleTimeout = 5000,
      size = 80,
      easterEggClicks = 5,
      easterEggMessage = "🎉 Yay!",
      ...props
    },
    ref
  ) => {
    const currentSection = useCurrentSection(sections);
    const isIdle = useIdleDetection(idleTimeout);
    const [clickCount, setClickCount] = React.useState(0);
    const [showEasterEgg, setShowEasterEgg] = React.useState(false);

    React.useEffect(() => {
      if (currentSection && onSectionChange) {
        onSectionChange(currentSection);
      }
    }, [currentSection, onSectionChange]);

    const getMascotMotion = (): MascotMotionType => {
      if (showEasterEgg) return MascotMotion.CELEBRATE;
      if (isIdle) return MascotMotion.WAVING;

      return sectionMotionMap[currentSection || "hero"] || MascotMotion.IDLE;
    };

    const handleClick = () => {
      setClickCount((prev) => prev + 1);
      if (clickCount >= easterEggClicks - 1) {
        setShowEasterEgg(true);
        setTimeout(() => {
          setShowEasterEgg(false);
          setClickCount(0);
        }, 3000);
      }
    };

    return (
      <div
        className={cn(
          "fixed right-6 bottom-6 z-40 hidden cursor-pointer transition-transform duration-300 hover:scale-110 lg:block",
          className
        )}
        onClick={handleClick}
        ref={ref}
        {...props}
      >
        <div className="relative">
          <div className="absolute inset-0 rounded-full bg-gradient-to-br from-[#4eaed0]/30 to-[#764ba2]/30 blur-xl" />
          <LegaliMascot
            className="relative z-10 drop-shadow-lg"
            height={size}
            motion={getMascotMotion()}
            width={size}
          />
          {showEasterEgg && (
            <div className="absolute -top-8 left-1/2 -translate-x-1/2 animate-bounce rounded-full bg-white px-3 py-1 font-bold text-[#4eaed0] text-xs shadow-lg">
              {easterEggMessage}
            </div>
          )}
        </div>
      </div>
    );
  }
);

FloatingMascot.displayName = "FloatingMascot";

export { FloatingMascot };

/**
 * @see {@link https://raw.githubusercontent.com/rryando/legali-ai-components/main/registry/legali-intake-chat-panel.json} — Install:
 * @example npx shadcn@latest add "https://raw.githubusercontent.com/rryando/legali-ai-components/main/registry/legali-intake-chat-panel.json"
 */
import { forwardRef, useCallback, useEffect, useRef, useState } from "react";
import { cn } from "@/lib/utils";
import { ChatInput } from "../atomic/ChatInput";
import { SuggestionChip } from "../atomic/SuggestionChip";
import type { ChatMessage, ContactInfo, IntakeChatScriptStep } from "../data/marketplace-types";
import { LegaliMascot, MascotMotion, type MascotMotionType } from "../mascot";
import { ChatWindow } from "./ChatWindow";
import { ContactInfoForm } from "./ContactInfoForm";

type IntakeChatPanelProps = {
  script: IntakeChatScriptStep[];
  onComplete?: (messages: ChatMessage[], contactInfo: ContactInfo | null) => void;
  className?: string;
};

function createMessage(sender: ChatMessage["sender"], text: string, id?: string): ChatMessage {
  return {
    id: id ?? `msg-${Date.now()}-${Math.random().toString(36).slice(2, 6)}`,
    sender,
    text,
    timestamp: new Date(),
  };
}

const IntakeChatPanel = forwardRef<HTMLDivElement, IntakeChatPanelProps>(
  ({ className, script, onComplete }, ref) => {
    const [messages, setMessages] = useState<ChatMessage[]>([]);
    const [scriptIndex, setScriptIndex] = useState(0);
    const [isAiTyping, setIsAiTyping] = useState(false);
    const [showContactForm, setShowContactForm] = useState(false);
    const [contactInfo, setContactInfo] = useState<ContactInfo | null>(null);
    const [suggestions, setSuggestions] = useState<string[]>([]);
    const [mascotMotion, setMascotMotion] = useState<MascotMotionType>(MascotMotion.WAVING);
    const [inputDisabled, setInputDisabled] = useState(true);
    const completedRef = useRef(false);

    // Fire the current script step (AI message)
    const fireStep = useCallback(
      (index: number) => {
        if (index >= script.length) return;
        const step = script[index];
        setIsAiTyping(true);
        setInputDisabled(true);
        setSuggestions([]);
        if (step.mascotMotion) setMascotMotion(step.mascotMotion);

        const delay = step.delayMs ?? 1200;
        const timer = setTimeout(() => {
          setMessages((prev) => [...prev, createMessage("ai", step.aiMessage)]);
          setIsAiTyping(false);

          if (step.showContactForm) {
            setShowContactForm(true);
            setInputDisabled(true);
          } else {
            setInputDisabled(false);
          }

          if (step.suggestions) {
            setSuggestions(step.suggestions);
          }
        }, delay);

        return () => clearTimeout(timer);
      },
      [script]
    );

    // Initial greeting
    useEffect(() => {
      return fireStep(0);
    }, [fireStep]);

    const advanceScript = useCallback(() => {
      const nextIndex = scriptIndex + 1;
      if (nextIndex >= script.length) {
        if (!completedRef.current) {
          completedRef.current = true;
          onComplete?.(messages, contactInfo);
        }
        return;
      }
      setScriptIndex(nextIndex);
      fireStep(nextIndex);
    }, [scriptIndex, script.length, fireStep, onComplete, messages, contactInfo]);

    const handleUserMessage = useCallback(
      (text: string) => {
        setMessages((prev) => [...prev, createMessage("user", text)]);
        setSuggestions([]);
        // Small delay before advancing
        setTimeout(advanceScript, 300);
      },
      [advanceScript]
    );

    const handleSuggestionClick = useCallback(
      (text: string) => {
        handleUserMessage(text);
      },
      [handleUserMessage]
    );

    const handleContactSubmit = useCallback(
      (info: ContactInfo) => {
        setContactInfo(info);
        setShowContactForm(false);
        setMessages((prev) => [
          ...prev,
          createMessage("system", `Contact details submitted: ${info.firstName} ${info.lastName}`),
        ]);
        setTimeout(advanceScript, 300);
      },
      [advanceScript]
    );

    return (
      <div
        className={cn(
          "flex h-[600px] flex-col overflow-hidden rounded-2xl border border-slate-200/50 bg-gradient-to-b from-slate-50/80 to-white/60 backdrop-blur-xl",
          className
        )}
        ref={ref}
      >
        {/* Header */}
        <div className="flex items-center gap-3 border-slate-200/40 border-b px-4 py-3">
          <div className="h-10 w-10">
            <LegaliMascot height={40} motion={mascotMotion} width={40} />
          </div>
          <div>
            <p className="font-semibold text-slate-800 text-sm">Legali AI Assistant</p>
            <p className="text-slate-400 text-xs">Your legal advisor</p>
          </div>
        </div>

        {/* Chat area */}
        <ChatWindow isTyping={isAiTyping} messages={messages} />

        {/* Contact form (slides in when needed) */}
        {showContactForm && <ContactInfoForm onSubmit={handleContactSubmit} />}

        {/* Suggestions */}
        {suggestions.length > 0 && (
          <div className="flex flex-wrap gap-2 border-slate-200/40 border-t px-4 py-2">
            {suggestions.map((s, i) => (
              <SuggestionChip
                animationDelay={i * 0.1}
                key={s}
                label={s}
                onClick={() => handleSuggestionClick(s)}
              />
            ))}
          </div>
        )}

        {/* Input */}
        <div className="border-slate-200/40 border-t p-3">
          <ChatInput disabled={inputDisabled || isAiTyping} onSend={handleUserMessage} />
        </div>
      </div>
    );
  }
);

IntakeChatPanel.displayName = "IntakeChatPanel";

export { IntakeChatPanel };
export type { IntakeChatPanelProps };

/**
 * @see {@link https://raw.githubusercontent.com/rryando/legali-ai-components/main/registry/legali-lawyer-profile-card.json} — Install:
