 */
import { Bell } from "lucide-react";
import * as React from "react";
import { cn } from "@/lib/utils";
import { SpotlightCard } from "../atomic/SpotlightCard";

export interface LiveTickerNotification {
  name: string;
  location: string;
  action: string;
}

export interface LiveTickerProps extends React.HTMLAttributes<HTMLDivElement> {
  /** List of notifications to cycle through */
  notifications?: LiveTickerNotification[];
  /** Interval between notifications in milliseconds */
  interval?: number;
  /** Icon to display */
  icon?: React.ReactNode;
  /** "from" connector text */
  fromLabel?: string;
  /** "just" connector text */
  justLabel?: string;
}

const defaultNotifications: LiveTickerNotification[] = [
  { name: "Sarah M.", location: "Colorado", action: "analyzed a contract" },
  { name: "David C.", location: "New York", action: "found a lawyer" },
  { name: "Marcus J.", location: "Texas", action: "built their case" },
  { name: "Emily R.", location: "California", action: "analyzed a lease" },
];

/**
 * A rotating notification ticker showing recent activity.
 * Displays one notification at a time with fade transitions.
 */
const LiveTicker = React.forwardRef<HTMLDivElement, LiveTickerProps>(
  (
    {
      className,
      notifications = defaultNotifications,
      interval = 4000,
      icon = <Bell className="h-4 w-4 text-[#4eaed0]" />,
      fromLabel = "from",
      justLabel = "just",
      ...props
    },
    ref
  ) => {
    const [currentIndex, setCurrentIndex] = React.useState(0);
    const [isVisible, setIsVisible] = React.useState(true);

    React.useEffect(() => {
      const timer = setInterval(() => {
        setIsVisible(false);
        setTimeout(() => {
          setCurrentIndex((prev) => (prev + 1) % notifications.length);
          setIsVisible(true);
        }, 500);
      }, interval);
      return () => clearInterval(timer);
    }, [notifications.length, interval]);

    const notification = notifications[currentIndex];

    return (
      <div
        className={cn(
          "fixed bottom-6 left-6 z-40 hidden transition-all duration-500 md:block",
          isVisible ? "translate-y-0 opacity-100" : "translate-y-4 opacity-0",
          className
        )}
        ref={ref}
        {...props}
      >
        <SpotlightCard className="flex items-center gap-3 rounded-xl bg-white/80 px-4 py-3 shadow-lg">
          {icon}
          <p className="text-slate-700 text-sm">
            <span className="font-semibold">{notification.name}</span>
            <span className="text-slate-500">
              {" "}
              {fromLabel} {notification.location}
            </span>
            <span className="text-slate-600">
              {" "}
              {justLabel} {notification.action}
            </span>
          </p>
        </SpotlightCard>
      </div>
    );
  }
);

LiveTicker.displayName = "LiveTicker";

export { LiveTicker };

/**
 * @see {@link https://raw.githubusercontent.com/rryando/legali-ai-components/main/registry/legali-matching-animation.json} — Install:
 * @example npx shadcn@latest add "https://raw.githubusercontent.com/rryando/legali-ai-components/main/registry/legali-matching-animation.json"
 */
import { AnimatePresence, motion } from "motion/react";
import { forwardRef, useEffect, useState } from "react";
import { cn } from "@/lib/utils";
import { MatchingPulse } from "../atomic/MatchingPulse";
import { LegaliMascot, MascotMotion, type MascotMotionType } from "../mascot";

type MatchingAnimationProps = {
  searchMessages: string[];
  matchDelayMs?: number;
  onMatchComplete?: () => void;
  className?: string;
};

const MatchingAnimation = forwardRef<HTMLDivElement, MatchingAnimationProps>(
  ({ className, searchMessages, matchDelayMs = 5000, onMatchComplete }, ref) => {
    const [messageIndex, setMessageIndex] = useState(0);
    const [isComplete, setIsComplete] = useState(false);
    const [mascotMotion, setMascotMotion] = useState<MascotMotionType>(MascotMotion.THINKING);

    // Cycle through search messages
    useEffect(() => {
      if (isComplete) return;

      const messageInterval = matchDelayMs / searchMessages.length;
      const timer = setInterval(() => {
        setMessageIndex((prev) => {
          const next = prev + 1;
          if (next >= searchMessages.length) {
            clearInterval(timer);
            return prev;
          }
          return next;
        });
      }, messageInterval);

      return () => clearInterval(timer);
    }, [isComplete, matchDelayMs, searchMessages.length]);

    // Completion timer
    useEffect(() => {
      const timer = setTimeout(() => {
        setIsComplete(true);
        setMascotMotion(MascotMotion.CELEBRATE);
        onMatchComplete?.();
      }, matchDelayMs);

      return () => clearTimeout(timer);
    }, [matchDelayMs, onMatchComplete]);

    // Mascot transitions
    useEffect(() => {
      if (isComplete) return;
      const halfway = matchDelayMs * 0.6;
      const timer = setTimeout(() => {
        setMascotMotion(MascotMotion.IDEA);
      }, halfway);
      return () => clearTimeout(timer);
    }, [isComplete, matchDelayMs]);

    return (
      <div
        className={cn("flex flex-col items-center justify-center gap-8 py-12", className)}
        ref={ref}
      >
        <div className="relative flex items-center justify-center">
          <MatchingPulse ringCount={isComplete ? 0 : 4} size={280} />
          <div className="absolute">
            <LegaliMascot height={120} motion={mascotMotion} width={120} />
          </div>
        </div>

        <AnimatePresence mode="wait">
          <motion.p
            animate={{ opacity: 1, y: 0 }}
            className={cn("text-center font-medium text-slate-600", isComplete && "text-[#4eaed0]")}
            exit={{ opacity: 0, y: -8 }}
            initial={{ opacity: 0, y: 8 }}
            key={messageIndex}
            transition={{ duration: 0.3 }}
          >
            {searchMessages[messageIndex]}
          </motion.p>
        </AnimatePresence>

        {!isComplete && (
          <div className="flex gap-1.5">
            {searchMessages.map((_, i) => (
              <div
                className={cn(
                  "h-1.5 w-1.5 rounded-full transition-colors duration-300",
                  i <= messageIndex ? "bg-[#4eaed0]" : "bg-slate-200"
                )}
                key={`dot-${searchMessages[i]}`}
              />
            ))}
          </div>
        )}
      </div>
    );
  }
);

MatchingAnimation.displayName = "MatchingAnimation";

export { MatchingAnimation };
export type { MatchingAnimationProps };

