/**
 * @see {@link https://raw.githubusercontent.com/rryando/legali-ai-components/main/registry/legali-floating-particles-system.json} — Install:
 * @example npx shadcn@latest add "https://raw.githubusercontent.com/rryando/legali-ai-components/main/registry/legali-floating-particles-system.json"
 */
import { BookOpen, FileText, Gavel, Scale, Sparkles } from "lucide-react";
import type { HTMLAttributes } from "react";
import { forwardRef, useMemo } from "react";
import { cn } from "@/lib/utils";
import { FloatingParticle } from "../atomic/FloatingParticle";

type FloatingParticlesSystemProps = {
  /** Number of particles to display */
  particleCount?: number;
  /** Enable mouse interaction */
  enableMouseInteraction?: boolean;
} & HTMLAttributes<HTMLDivElement>;

const LEGAL_ICONS = [
  { icon: Scale, color: "text-[#4eaed0]", glow: "rgba(78, 174, 208, 0.3)" },
  { icon: Gavel, color: "text-[#667eea]", glow: "rgba(102, 126, 234, 0.3)" },
  { icon: FileText, color: "text-[#764ba2]", glow: "rgba(118, 75, 162, 0.3)" },
  { icon: BookOpen, color: "text-[#f472b6]", glow: "rgba(244, 114, 182, 0.3)" },
  { icon: Sparkles, color: "text-[#06b6d4]", glow: "rgba(6, 182, 212, 0.3)" },
];

/**
 * Container that renders multiple floating particles with
 * randomized properties. Uses legal-themed icons.
 *
 * @example
 * ```tsx
 * <FloatingParticlesSystem particleCount={8} />
 * ```
 */
const FloatingParticlesSystem = forwardRef<HTMLDivElement, FloatingParticlesSystemProps>(
  ({ className, particleCount = 6, enableMouseInteraction = false, ...props }, ref) => {
    const particles = useMemo(() => {
      return Array.from({ length: particleCount }, (_, i) => {
        const iconData = LEGAL_ICONS[i % LEGAL_ICONS.length];
        const Icon = iconData.icon;

        // Distribute particles around the container edges
        const angle = (i / particleCount) * 360;
        const radius = 35 + Math.random() * 15; // 35-50% from center

        // Convert polar to cartesian coordinates
        const x = 50 + Math.cos((angle * Math.PI) / 180) * radius;
        const y = 50 + Math.sin((angle * Math.PI) / 180) * radius;

        return {
          id: i,
          icon: <Icon className={cn("h-5 w-5", iconData.color)} />,
          size: 36 + Math.random() * 16, // 36-52px
          duration: 12 + Math.random() * 8, // 12-20s
          delay: Math.random() * 5,
          glowColor: iconData.glow,
          position: { x, y },
        };
      });
    }, [particleCount]);

    return (
      <div className={cn("pointer-events-none absolute inset-0", className)} ref={ref} {...props}>
        {particles.map((particle) => (
          <FloatingParticle
            delay={particle.delay}
            duration={particle.duration}
            glowColor={particle.glowColor}
            icon={particle.icon}
            initialPosition={particle.position}
            key={particle.id}
            size={particle.size}
          />
        ))}
      </div>
    );
  }
);

FloatingParticlesSystem.displayName = "FloatingParticlesSystem";

export { FloatingParticlesSystem };
export type { FloatingParticlesSystemProps };

/**
 * @see {@link https://raw.githubusercontent.com/rryando/legali-ai-components/main/registry/legali-interactive-stats-card.json} — Install:
 * @example npx shadcn@latest add "https://raw.githubusercontent.com/rryando/legali-ai-components/main/registry/legali-interactive-stats-card.json"
 */
import { motion } from "motion/react";
import type { MouseEvent } from "react";
import { forwardRef, useCallback, useState } from "react";
import { cn } from "@/lib/utils";

type InteractiveStatsCardProps = {
  /** Main statistic value */
  value: string | number;
  /** Label describing the stat */
  label: string;
  /** Optional prefix (e.g., "$") */
  prefix?: string;
  /** Optional suffix (e.g., "%", "K+") */
  suffix?: string;
  /** Color accent for the card */
  accentColor?: string;
  /** Enable sparkline on hover */
  showSparkline?: boolean;
  /** Additional CSS classes */
  className?: string;
};

/**
 * Enhanced stat card with hover effects and optional sparkline.
 * Features glassmorphism and 3D lift on hover.
 *
 * @example
 * ```tsx
 * <InteractiveStatsCard
 *   value={50}
 *   suffix="K+"
 *   label="Cases Analyzed"
 * />
 * ```
 */
const InteractiveStatsCard = forwardRef<HTMLDivElement, InteractiveStatsCardProps>(
  (
    {
      className,
      value,
      label,
      prefix = "",
      suffix = "",
      accentColor = "#4eaed0",
      showSparkline = true,
    },
    ref
  ) => {
    const [isHovered, setIsHovered] = useState(false);
    const [mousePos, setMousePos] = useState({ x: 50, y: 50 });

    const handleMouseMove = useCallback((e: MouseEvent<HTMLDivElement>) => {
      const rect = e.currentTarget.getBoundingClientRect();
      setMousePos({
        x: ((e.clientX - rect.left) / rect.width) * 100,
        y: ((e.clientY - rect.top) / rect.height) * 100,
      });
    }, []);

    // Simple sparkline data (would be real data in production)
    const sparklinePoints =
      "M0,20 L10,15 L20,18 L30,10 L40,12 L50,5 L60,8 L70,3 L80,6 L90,4 L100,2";

    return (
      <motion.div
        animate={{
          y: isHovered ? -4 : 0,
          scale: isHovered ? 1.02 : 1,
        }}
        className={cn(
          "group relative overflow-hidden rounded-2xl p-6",
          "border border-white/40 bg-white/60 backdrop-blur-xl",
          isHovered && "shadow-2xl shadow-slate-200/50",
          className
        )}
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
        onMouseMove={handleMouseMove}
        ref={ref}
        transition={{ type: "spring", stiffness: 400, damping: 25 }}
      >
        {/* Spotlight effect */}
        <div
          className="pointer-events-none absolute inset-0 opacity-0 transition-opacity duration-300 group-hover:opacity-100"
          style={{
            background: `radial-gradient(400px circle at ${mousePos.x}% ${mousePos.y}%, ${accentColor}15, transparent 60%)`,
          }}
        />

        {/* Content */}
        <div className="relative z-10">
          <div className="flex items-baseline gap-1">
            {prefix ? <span className="font-semibold text-lg text-slate-400">{prefix}</span> : null}
            <span
              className="font-bold text-4xl tabular-nums"
              style={{ color: isHovered ? accentColor : "#0f172a" }}
            >
              {value}
            </span>
            {suffix ? <span className="font-semibold text-lg text-slate-400">{suffix}</span> : null}
          </div>
          <p className="mt-1 text-slate-500 text-sm">{label}</p>

          {/* Sparkline on hover */}
          {showSparkline ? (
            <motion.div
              animate={{
                opacity: isHovered ? 1 : 0,
                height: isHovered ? 24 : 0,
              }}
              className="mt-3 overflow-hidden"
              initial={{ opacity: 0, height: 0 }}
              transition={{ duration: 0.2 }}
            >
              <svg
                aria-label={`Sparkline chart for ${label}`}
                className="h-6 w-full"
                preserveAspectRatio="none"
                role="img"
                viewBox="0 0 100 24"
              >
                <title>Trend chart for {label}</title>
                <defs>
                  <linearGradient id={`sparkline-gradient-${label}`} x1="0" x2="0" y1="0" y2="1">
                    <stop offset="0%" stopColor={accentColor} stopOpacity="0.3" />
                    <stop offset="100%" stopColor={accentColor} stopOpacity="0" />
                  </linearGradient>
                </defs>
                {/* Fill area */}
                <path
                  d={`${sparklinePoints} L100,24 L0,24 Z`}
                  fill={`url(#sparkline-gradient-${label})`}
                />
                {/* Line */}
                <path
                  d={sparklinePoints}
                  fill="none"
                  stroke={accentColor}
                  strokeLinecap="round"
                  strokeWidth="2"
                />
              </svg>
            </motion.div>
          ) : null}
        </div>

        {/* Bottom accent line */}
        <motion.div
          animate={{ scaleX: isHovered ? 1 : 0 }}
          className="absolute bottom-0 left-0 h-0.5 w-full origin-left"
          initial={{ scaleX: 0 }}
          style={{ backgroundColor: accentColor }}
          transition={{ duration: 0.3 }}
        />
      </motion.div>
    );
  }
);

InteractiveStatsCard.displayName = "InteractiveStatsCard";

export { InteractiveStatsCard };
export type { InteractiveStatsCardProps };

