const SmartSuggestionsDropdown = forwardRef<HTMLDivElement, SmartSuggestionsDropdownProps>(
  (
    { className, isOpen = false, suggestions = defaultSuggestions, onSelect, maxSuggestions = 5 },
    ref
  ) => {
