import {
  type MotionProps,
  motion,
  type UseInViewOptions,
  useInView,
  type Variants,
} from "motion/react";
import { useEffect, useRef, useState } from "react";
import { cn } from "@/lib/utils";

type AnimationVariant =
  | "fadeIn"
  | "blurIn"
  | "blurInUp"
  | "blurInDown"
  | "slideUp"
  | "slideDown"
  | "slideLeft"
  | "slideRight"
  | "scaleUp"
  | "scaleDown";

interface TypingTextProps extends Omit<MotionProps, "children"> {
  /** Text to animate */
  text?: string;
  /** Array of texts to cycle through */
  texts?: string[];
  /** Typing speed in milliseconds */
  speed?: number;
  /** Delay before starting animation */
  delay?: number;
  /** Whether to show cursor */
  showCursor?: boolean;
  /** Cursor character */
  cursor?: string;
  /** Cursor className */
  cursorClassName?: string;
  /** Whether to loop through texts */
  loop?: boolean;
  /** Pause duration between loops */
  pauseDuration?: number;
  /** Custom className */
  className?: string;
  /** Callback when typing completes */
  onComplete?: () => void;
  /** Whether to start animation when component enters viewport */
  startOnView?: boolean;
  /** Whether to animate only once */
  once?: boolean;
  /** The animation preset to use */
  animation?: AnimationVariant;
  /** Margin for in-view detection (rootMargin) */
  inViewMargin?: UseInViewOptions["margin"];
}

const cursorVariants: Variants = {
  blinking: {
    opacity: [0, 0, 1, 1],
    transition: {
      duration: 1,
      repeat: Number.POSITIVE_INFINITY,
      repeatDelay: 0,
      ease: "linear",
      times: [0, 0.5, 0.5, 1],
    },
  },
};

export function TypingText({
  text,
  texts,
  speed = 50,
  delay = 0,
  showCursor = true,
  cursorClassName = "",
  cursor = "|",
  loop = false,
  pauseDuration = 2000,
  className,
  onComplete,
  startOnView = true,
  once = false,
  inViewMargin,
  ...props
}: TypingTextProps) {
  const ref = useRef<HTMLSpanElement>(null);
  const isInView = useInView(ref, {
    once,
    margin: inViewMargin as UseInViewOptions["margin"],
  });
  const [hasAnimated, setHasAnimated] = useState(false);
  const [displayText, setDisplayText] = useState("");
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isTyping, setIsTyping] = useState(false);
  const [currentTextIndex, setCurrentTextIndex] = useState(0);

  // Determine if we should start animation
  const shouldStart = !startOnView || (isInView && !(once && hasAnimated));

  // Reset typing when input text(s) change
  const textSignature = (texts && texts.length > 0 ? texts : [text])
    .map((t) => t ?? "")
    .join("\u0000");

  useEffect(() => {
    setDisplayText("");
    setCurrentIndex(0);
    setCurrentTextIndex(0);
    setIsTyping(false);
    setHasAnimated(false);
  }, [textSignature]);

  const textArray = texts && texts.length > 0 ? texts : [text];
  const currentText = textArray[currentTextIndex] ?? "";

  useEffect(() => {
    if (!shouldStart) return;
    const timeout = setTimeout(() => {
      setIsTyping(true);
      setHasAnimated(true);
    }, delay);

    return () => clearTimeout(timeout);
  }, [delay, shouldStart, textSignature]);

  useEffect(() => {
    if (!isTyping) return;

    if (currentIndex < currentText.length) {
      const timeout = setTimeout(() => {
        setDisplayText(currentText.slice(0, currentIndex + 1));
        setCurrentIndex(currentIndex + 1);
      }, speed);

      return () => clearTimeout(timeout);
    }
    // Typing complete
    onComplete?.();

    if (texts && texts.length > 1) {
      const isLast = currentTextIndex >= texts.length - 1;
      if (loop || !isLast) {
        const nextIndex = loop ? (currentTextIndex + 1) % texts.length : currentTextIndex + 1;
        const timeout = setTimeout(() => {
          setDisplayText("");
          setCurrentIndex(0);
          setCurrentTextIndex(nextIndex);
        }, pauseDuration);

        return () => clearTimeout(timeout);
      }
    }
  }, [currentIndex, currentText, isTyping, speed, loop, texts, pauseDuration, onComplete]);

  // Animation variants for container (fadeIn by default, extendable)
  const finalVariants = {
    container: {
      hidden: { opacity: 0, y: 10 },
      show: { opacity: 1, y: 0, transition: { staggerChildren: 0.02 } },
      exit: { opacity: 0 },
    },
  };
  const MotionComponent = motion.span;

  return (
    <MotionComponent
      animate={startOnView ? undefined : "show"}
      className={cn("whitespace-pre-wrap", className)}
      exit="exit"
      initial="hidden"
      ref={ref}
      variants={finalVariants.container as Variants}
      viewport={{ once }}
      whileInView={startOnView ? "show" : undefined}
      {...props}
    >
      <span style={{ display: "inline-flex", alignItems: "center" }}>
        {displayText}
        {showCursor && (
          <motion.span
            animate="blinking"
            className={cn(
              "ms-1 inline-block w-px select-none font-normal text-foreground",
              cursorClassName
            )}
            variants={cursorVariants}
          >
            {cursor}
          </motion.span>
        )}
      </span>
    </MotionComponent>
  );
}

