            ))}
          </nav>
        </div>

        {/* Main Content Area */}
        <div className="relative flex h-full flex-1 flex-col overflow-hidden">
          {activeTab === "learn" && (
            <>
              {/* Header Area (Mobile Only for Stats) */}
